package cms;import java.util.concurrent.atomic.AtomicInteger;public class Author {protected static AtomicInteger idCounter = new AtomicInteger(1);protected int authorId;protected String name;protected String email;protected String bio;protected String role; protected int publishedCount;public Author(String name, String email, String bio, String role) {this.authorId = idCounter.getAndIncrement();this.name = name;this.email = email;this.bio = bio;this.role = role;this.publishedCount = 0;}public int getAuthorId() { return authorId; }public String getName() { return name; }public void setName(String name) { this.name = name; }public String getEmail() { return email; }public void setEmail(String email) { this.email = email; }public String getBio() { return bio; }public void setBio(String bio) { this.bio = bio; }public String getRole() { return role; }public void setRole(String role) { this.role = role; }public int getPublishedCount() { return publishedCount; }public void incrementPublishedCount() { this.publishedCount++; }public boolean canPublish(Post post) {return post.getContent() != null && post.getContent().length() >= 20;}@Overridepublic String toString() {return String.format("Author[%d] %s (%s)", authorId, name, role);}}
