package cms;import java.util.concurrent.atomic.AtomicInteger;public class Category {private static final AtomicInteger idCounter = new AtomicInteger(0);private final int categoryId;private String name;private String description;private Category parent;public Category(String name, String description, Category parent) { this.categoryId = idCounter.incrementAndGet();this.name = name;this.description = description;this.parent = parent;}public int getCategoryId() { return categoryId; }public String getName() { return name; }public void setName(String name) { this.name = name; }public String getDescription() { return description; }public void setDescription(String description) { this.description = description; }public Category getParent() { return parent; }public void setParent(Category parent) { this.parent = parent; }@Overridepublic String toString() {return String.format("Category[%d] %s",categoryId,name == null ? "<unnamed>" : name);}}
