package cms;public class GuestAuthor extends Author {private String affiliation;private int maxPostsAllowed;public GuestAuthor(String name, String email, String bio, String affiliation, int maxPostsAllowed) {super(name, email, bio, "Guest");this.affiliation = affiliation;this.maxPostsAllowed = maxPostsAllowed;}public String getAffiliation() { return affiliation; }public void setAffiliation(String affiliation) { this.affiliation = affiliation; }public int getMaxPostsAllowed() { return maxPostsAllowed; }public void setMaxPostsAllowed(int maxPostsAllowed) { this.maxPostsAllowed = maxPostsAllowed; }@Overridepublic boolean canPublish(Post post) {if (getPublishedCount() >= maxPostsAllowed) {return false;}return post.getContent() != null && post.getContent().length() >= 50;}}
